import { AppRegistry, Platform } from 'react-native-web';
export default (function (componentName, component) {
  if (Platform.OS === 'web') {
    AppRegistry.registerComponent(componentName, function () {
      return component;
    });
    AppRegistry.runApplication(componentName, {
      rootTag: document.getElementById('root')
    });
  } else {
    throw new Error('the platform is not web');
  }
});